function cancel_so(){redirect("cms/signatories/so")}function submit_so(){wait_msg(message.loading)}function search_so(){wait_msg(message.loading)}
function approve_so(){$(".success").hide();$(".notice").hide();var a=get_ids("id");if(a.length<1){error_msg(message.noselection);return false}wait_msg(message.loading);$.ajax({url:base_url+"cms/signatories/so/approve",type:"POST",dataType:"json",data:{id:a},success:function(b){b.success==true&&redirect("cms/signatories/so/index/MG4zUmExbTBMYXBwcm92ZQ==")},error:function(){error_msg(message.ajaxerror)}});return false}
function disapprove_so(){$(".success").hide();$(".notice").hide();var a=get_ids("id");if(a.length<1){error_msg(message.noselection);return false}wait_msg(message.loading);$.ajax({url:base_url+"cms/signatories/so/disapprove",type:"POST",dataType:"json",data:{id:a},success:function(b){b.success==true&&redirect("cms/signatories/so/index/MG4zUmExbTBMZGlzYXBwcm92ZQ==")},error:function(){error_msg(message.ajaxerror)}});return false};
