function cancel_customer(){redirect("cms/accounts/customer")}function submit_customer(){wait_msg(message.loading);$("#customer-form").ajaxSubmit(function(a){var b=JSON.parse(a);if(b.success==true){if(b.action=="add"){redirect("cms/accounts/customer/index/MG4zUmExbTBMYWRk")}else{if(b.action=="edit"){redirect("cms/accounts/customer/index/MG4zUmExbTBMZWRpdA==")}}}else{error_msg(b.failmessage)}})}function delete_customer(){$(".success").hide();$(".notice").hide();var a=get_ids("id");if(a.length<1){error_msg(message.noselection);return false}wait_msg(message.loading);var b={url:base_url+"cms/accounts/customer/delete",type:"POST",dataType:"json",data:{id:a},success:function(c){if(c.success==true){redirect("cms/accounts/customer/index/MG4zUmExbTBMZGVsZXRl")}},error:function(){error_msg(message.ajaxerror)}};$.ajax(b);return false}function enable_customer(){$(".success").hide();$(".notice").hide();var a=get_ids("id");if(a.length<1){error_msg(message.noselection);return false}wait_msg(message.loading);var b={url:base_url+"cms/accounts/customer/enable",type:"POST",dataType:"json",data:{id:a},success:function(c){if(c.success==true){redirect("cms/accounts/customer/index/MG4zUmExbTBMZW5hYmxl")}},error:function(){error_msg(message.ajaxerror)}};$.ajax(b);return false}function disable_customer(){$(".success").hide();$(".notice").hide();var a=get_ids("id");if(a.length<1){error_msg(message.noselection);return false}wait_msg(message.loading);var b={url:base_url+"cms/accounts/customer/disable",type:"POST",dataType:"json",data:{id:a},success:function(c){if(c.success==true){redirect("cms/accounts/customer/index/MG4zUmExbTBMZGlzYWJsZQ==")}},error:function(){error_msg(message.ajaxerror)}};$.ajax(b);return false};